#include "nbind/api.h"

struct ObjA
{
    ObjA(void)
        : a(0), b(0)
    {}

    ObjA(int a, int b)
        : a(a), b(b)
    {}

    void toJS(nbind::cbOutput output)
    {
        output(a, b);
    }

    int a;
    int b;
};

struct TestA
{
    ObjA test(void)
    {
        return ObjA(42, 42);
    }
};

#include "nbind/nbind.h"

NBIND_CLASS(ObjA)
{
    construct<>();
    construct<int,int>();
}

NBIND_CLASS(TestA)
{
    construct<>();

    method(test);
}
