#include "nbind/api.h"

struct ObjB
{
    ObjB(void)
        : a(0), b(0)
    {}

    ObjB(int a, int b)
        : a(a), b(b)
    {}

    void toJS(nbind::cbOutput output)
    {
        output(a, b);
    }

    int a;
    int b;
};

struct TestB
{
    ObjB test(void)
    {
        return ObjB(42, 42);
    }
};

#include "nbind/nbind.h"

NBIND_CLASS(ObjB)
{
    construct<>();
    construct<int,int>();
}

NBIND_CLASS(TestB)
{
    construct<>();

    method(test);
}
